// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require('electron-squirrel-startup')) {
  app.quit();
}


let mainWindow:BrowserWindow;
const createWindow = (): void => {
  // Create the browser window.
   mainWindow = new BrowserWindow({
    height: 600,
    width: 800,
    frame:false,// لاخفاء المنطقه العلويه
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
    },
  });

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);
 process.platform == "darwin" && mainWindow.setWindowButtonVisibility(true); // ازرار خاصه    اظهار macOS
  // Open the DevTools.
  mainWindow.webContents.openDevTools();

  mainWindow.webContents.on("did-finish-load", () => {
    console.log("Window finished loading");

  get_all_notes((data: INoteData) => {
    console.log("all notes",data);
    mainWindow.webContents.send('onstart-notes-data', data)
  })
   });
};


ipcMain.handle('fetch-all-notes', async (event, argz) => {
    return await new Promise((resolve, reject) => {
      get_all_notes((data:any)=>{
        console.log("all notes-fetch",data);
      resolve(data);
      })

    })

})
ipcMain.handle('delete-note', async (event, note_id) => {
    return await new Promise((resolve, reject) => {
      delete_note(note_id,(data:any)=>{
      resolve(data);
      })

    })

})

ipcMain.on('close-app',(ev,argz)=>{
  mainWindow.close()
})
ipcMain.on('maximize-app',(ev,argz)=>{
 mainWindow.isMaximized() ? mainWindow.unmaximize():  mainWindow.maximize()
})
ipcMain.on('minimize-app',(ev,argz)=>{
  mainWindow.minimize()
})
          
