ipcMain.handle('set-note', async (event, argz) => {
    // ev.sender.send()
    return await new Promise((resolve, reject) => {
      set_notes(argz, (data: any) => {
        resolve(data);
      })
    })
  })

function openNoteWindow(note_id: string) {
    const  childWindow = new BrowserWindow({
    height: 450,
    width: 450,
    frame:false,// لاخفاء المنطقه العلويه
    webPreferences: {
      preload: CHILD_WINDOW_PRELOAD_WEBPACK_ENTRY,
      // nodeIntegration: false,
      // contextIsolation: true,
      // sandbox: false
    },

  });      
  childWindow.webContents.openDevTools();
  childWindow.loadURL(CHILD_WINDOW_WEBPACK_ENTRY);
  
ipcMain.on("close-child-window", () => {
    if (childWindow && !childWindow.isDestroyed()) {
        childWindow.close();
    }
});

  childWindow.webContents.on("did-finish-load", () => {
    // console.log("yessss", note_id )
    childWindow.webContents.send("child-note-id", note_id);
  }
  
)};

  ipcMain.on('open-note-item-context-menu',(ev,note_id)=>{
  const template = [
    {
      label: 'Open in a new window',
      click: () => { 
        // console.log("note id", note_id);
        
        openNoteWindow(note_id)
      }
    },
    { type: 'separator' },
    { label: 'Delete', click: () => {
      delete_note(note_id, (data: string) => {
        // console.log("all notes", data);
        ev.sender.send('update-notes-data', data)
      })
    } }
  ] as any
  const menu = Menu.buildFromTemplate(template)
  menu.popup({ window: BrowserWindow.fromWebContents(ev.sender) })

})

ipcMain.handle('get-note', async (event, note_id: string) => {
  return new Promise((resolve, reject) => {
    get_note(note_id, (data: any) => {
      if (!data) {
        return resolve(null); // فقط إذا ما فيه data
      }
      
      try {
        const parsedNote: INoteData = {
          id: data.id,
          note: JSON.parse(data.note),
        };
        resolve(parsedNote);
      } catch (e) {
        reject(e); // لو صار خطأ بالـ JSON
      }
    });
  });
});
// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', createWindow);

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

